import React, { useState } from "react";

const Contact = () => {
  const [data, setData] = useState({
    fullname: "",
    phone: "",
    email: "",
    msg: "",
  });
  const InputEvent = (event) => {
    const { name, value } = event.target;
    setData((prevVal) => {
      return {
        ...preVal,
        [name]: value,
      };
    });
  };
  const formSubmit = () => {};
  return (
    <>
      <div className="my-5">
        <h1 className="text-center">Contact Us</h1>
        <div className="container contact_div">
          <div className="row">
            <div className="col-md-6 col-10 mx-auto">
              <form onSubmit={formSubmit}>
                <div class="mb-3">
                  <label for="exampleFormControlInput1" class="form-label">
                    FullName
                  </label>
                  <input
                    value={data.fullname}
                    onChange={InputEvent}
                    type="text"
                    class="form-control"
                    id="exampleFormControlInput1"
                    placeholder="FullName Here"
                  />
                </div>

                <div class="mb-3">
                  <label for="exampleFormControlInput1" class="form-label">
                    Phone Number
                  </label>
                  <input
                    value={data.phone}
                    onChange={InputEvent}
                    type="phone"
                    class="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Phone Number Here"
                  />
                </div>
                <div class="mb-3">
                  <label for="exampleFormControlInput1" class="form-label">
                    Email
                  </label>
                  <input
                    value={data.email}
                    onChange={InputEvent}
                    type="text"
                    class="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Email Here"
                  />
                </div>
                <div class="mb-3">
                  <label for="exampleFormControlTextarea1" class="form-label">
                    Message
                  </label>
                  <textarea
                    value={data.msg}
                    onChange={InputEvent}
                    class="form-control"
                    id="exampleFormControlTextarea1"
                    rows="3"
                  ></textarea>
                </div>
                <div class="col-12">
                  <button class=" btn btn-outline-primary" type="submit">
                    Submit
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Contact;
