import React, { useState } from 'react'

const App = () => {
    const Color = '#8e44ad';
    const BtnColor = '#ece8ee';
    const [bg,setbg] = useState(Color);
    const [Name,setName] = useState('Click Me');
    const [btnbg,setbtnbg] = useState(BtnColor);
    const bgChange = () =>{
        let newbg = 'cyan';
        const newName = 'Well Done ðŸ˜€';
        setbg(newbg);
        setName(newName);
    }
    const bgBack = () =>{
        let newbg = 'red';
        const newName = 'Angry Double Click kyu kiya ? ðŸ˜¡';
        setbtnbg(newbg);
        setbg(newbg);
        setName(newName);
    }
    return(
       <>
       <div style={{backgroundColor:bg}}>
           <button onClick={bgChange} onDoubleClick={bgBack} style={{backgroundColor:BtnColor}}>{Name}</button>

       </div>
       </> 
    )
}
export default App;
