import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import AddIcon from "@material-ui/icons/Add";
const CreateNote = () => {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });
  const InputEvent = (event) => {
    const { value, name } = event.target.value;
    setNote((prevData) => {
      return {
        ...prevData,
        [name]: value,
      };
    });
    console.log(note);
  };
  return (
    <>
      <div className="main_note">
        <form action="">
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={note.title}
            onChange={InputEvent}
          />
          <textarea
            rows=""
            column=""
            onChange={InputEvent}
            name="content"
            placeholder="Write a Note..."
            autoComplete="off"
          ></textarea>
          <Button>
            <AddIcon />
          </Button>
        </form>
      </div>
    </>
  );
};

export default CreateNote;
