import React, { useState } from 'react'

const App = () => {
    const [Name,setName] = useState('');
    const [FullName,setFullName] = useState();
    const inputEvent = (event) =>{
        setName(event.target.value);
    }
    const clickbutton = (event) =>{
        event.preventDefault();
        alert("Your Data is Submited ! 🤯")

        setFullName(Name);
    };
    return(<>
    <form onSubmit={clickbutton}>
    <div>
        <h1>Hello <span>{FullName}</span></h1>
        <input  type="text" placeholder="Enter Your Name" name='Name' onChange={inputEvent}/>
        <input  type="email" placeholder="Enter Your Email" name='Name' onChange={inputEvent}/>
        <input  type="phone" placeholder="Enter Your Phone" name='Name' onChange={inputEvent}/>
        <button >Submit Me 👍🏻</button>
    </div>
    </form>
    </>)
};
export default App;
